module.exports = async d => {
    const code = d.command.code

    const r = code.split("$platform").length - 1

    const after = code.split("$platform")[r]

    if (after.startsWith("[")) {
        const inside = after.slice(1).split("]")[0]

        const [
            userID,
            separator = ", "
        ] = inside.split(";")

        const user = await d.client.users.fetch(userID).catch(Err => {})

        if (!user) return d.error(`:x: Invalid user ID in \`$platform[${inside}]\``)

        return {
            code: code.replaceLast(`$platform[${inside}]`, user.presence.status === "offline" ? "none" : Object.keys(user.presence.clientStatus).join(separator))
        }
    } else {
        return {
            code: code.replaceLast(`$platform`, d.message.author.presence.status === "offline" ? "none" : Object.keys(d.message.author.presence.clientStatus).join(", "))
        }
    }
}