module.exports = async d => {
    const code = d.command.code
    
    const r = code.split("$randomText[").length - 1

    const inside = code.split("$randomText[")[r].split("]")[0]

    let output = inside.split(";")[Math.floor(Math.random() * inside.split(";").length)]

    if (d.randoms[inside] === undefined) d.randoms[inside] = output
    else output = d.randoms[inside]

    return {
        randoms: d.randoms,
        code: code.replaceLast(`$randomText[${inside}]`, output)
    }
}