
module.exports = async d => {
    const code = d.command.code

    const r = code.split("$reactionRoleAdd[").length - 1

    const inside = code.split("$reactionRoleAdd[")[r].split("]")[0]

    const [
        channelID,
        messageID,
        emoji,
        roleID
    ] = inside.split(";")

    const channel = d.message.guild.channels.cache.get(channelID)

    if (!channel) return d.error(`:x: Invalid channel ID in \`$reactionRoleAdd[${inside}]\``)

    const msg = await channel.messages.fetch(messageID).catch(Err => {})

    if (!msg) return d.error(`Invalid message ID in \`$reactionRoleAdd[${inside}]\``)

    const role = d.message.guild.roles.cache.get(roleID)

    if (!role) return d.error(`:x: Invalid role ID in \`$reactionRoleAdd[${inside}]\``)

    const react = await msg.react(emoji).catch(Err => {})

    if (!react) return d.error(`:x: Failed to react with '${emoji}'!`)

    return {
        code: code.replaceLast(`$reactionRoleAdd[${inside}]`, "")
    }
}