
module.exports = async d => {
    const code = d.command.code

    const r = code.split("$reactionRoleRemove[").length - 1

    const inside = code.split("$reactionRoleRemove[")[r].split("]")[0]

    const [
        channelID,
        messageID,
        emoji
    ] = inside.split(";")

    const channel = d.message.guild.channels.cache.get(channelID)

    if (!channel) return d.error(`:x: Invalid channel ID in \`$reactionRoleRemove[${inside}]\``)

    const msg = await channel.messages.fetch(messageID).catch(Err => {})

    if (!msg) return d.error(`Invalid message ID in \`$reactionRoleRemove[${inside}]\``)

    return {
        code: code.replaceLast(`$reactionRoleRemove[${inside}]`, "")
    }
}