module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$removeSplitTextElement[").length - 1
    
    const inside = code.split("$removeSplitTextElement[")[r].split("]")[0]
    
    const elements = inside.split(";")
    
    return {
        array: d.array.filter((x, y) => !elements.includes(String(y + 1))), 
        code: code.replaceLast(`$removeSplitTextElement[${inside}]`, "")
    }
} 