module.exports = async d => {
    const code = d.command.code

    const r =code.split("$removeTextSplitElement[").length - 1

    const inside = code.split("$removeTextSplitElement[")[r].split("]")[0]

    const n = Number(inside)

    if (!n) return d.error(`:x: Invalid number in \`$removeTextSplitElement[${inside}]\``)

    return {
        array: d.array.filter((value, y) => y !== n - 1),
        code: code.replaceLast(`$removeTextSplitElement[${inside}]`, "")
    }
}