module.exports = async d => {
    const code = d.command.code

    const r = code.split("$repeatMessage[").length - 1

    const inside = code.split("$repeatMessage[")[r].split("]")[0]

    const [
        number,
        text
    ] = inside.split(";")

    const n = Number(number)

    if (!n) return d.error(`:x: Invalid number in \`$repeatMessage[${inside}]\``)

    return{
        code: code.replaceLast(`$repeatMessage[${inside}]`, text.repeat(n))
    }
}