const replaceText = async d => {

    const code = d.command.code

    const r = code.split("$replaceText[").length - 1

    const inside = code.split("$replaceText[")[r].split("]")[0]

    const [ text, match, replace ] = inside.split(";")
    
    if (replace === undefined) return d.error(`‚ùå Not enough fields in \`$replaceText[${inside}]\``) 
    
    return {
        code: code.replaceLast(`$replaceText[${inside}]`, text.addBrackets().split(match.addBrackets()).join(replace.addBrackets()).removeBrackets())
    }
}

module.exports = replaceText