module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$replaceTextWithRegex").length - 1 
    
    const inside = code.split("$replaceTextWithRegex[")[r].split("]")[0] 
    let [text, reg, flags, newT] = inside.split(";") 
    
    reg = reg.startsWith("/") && reg.endsWith("/") ? reg.slice(1, reg.length - 1) : reg
    
    if (newT === undefined) return d.error(`‚ùå Invalid amount of fields in \`$replaceTextWithRegex[${inside}]\``) 
    
    return {
        code: code.replaceLast(`$replaceTextWithRegex[${inside}]`, text.addBrackets().replace(new RegExp(reg.addBrackets(), flags || undefined), newT.addBrackets()).deleteBrackets())
    }
}