module.exports = async d => {
    const code = d.command.code

    const r = code.split("$resetVar[").length - 1

    const inside = code.split("$resetVar[")[r].split("]")[0]

    const all = await d.client.db.find({
            varID: "0",
            variable: inside
    })

    if (d.client.variables[inside] === undefined) return d.error(`:x: Invalid variable '${inside}' in \`$resetVar[${inside}]\``)

    for (const data of all) {
        d.client.db.set({
                variable: inside,
                varID: data["varID"]
        })
    }

    return {
        code: code.replaceLast(`$resetVar[${inside}]`, "")
    }
}