module.exports = async d => {
  
  const code = d.command.code
  
  const r = code.split("$roleExists").length - 1
  
  const inside = code.split("$roleExists[")[r].split("]")[0]
  
  const role = d.message.guild.roles.cache.get(inside) 
  
  return {
    code: code.replaceLast(`$roleExists[${inside}]`, role ? true : false) 
  } 
}

