module.exports = d => {
 
  const code = d.command.code
  
  const r = code.split("$roleID").length - 1
  
  const inside = code.split("$roleID[")[r].split("]")[0]
  
  const role = d.message.guild.roles.cache.find(r => r.name.toLowerCase() === inside.toLowerCase().addBrackets())
  
  if (!role) return d.error(`‚ùå Invalid role name in \`$roleID[${inside}]\``) 
  
  return {
    code: code.replaceLast(`$roleID[${inside}]`, role.id)
  } 
} 