module.exports = async d => {
    const code = d.command.code

    const r = code.split("$roleName[").length - 1

    const inside = code.split("$roleName[")[r].split("]")[0]

    const role = d.message.guild.roles.cache.get(inside)

    if (!role) return d.error(`:x: Invalid role ID in \`$roleName[${inside}]\``)

    return {
        code: code.replaceLast(`$roleName[${inside}]`, role.name)
    }
}