module.exports = async d => {
    const code = d.command.code

    const r = code.split("$roundTenth[").length - 1
    
    const inside = code.split("$roundTenth[")[r].split("]")[0]

    const n = Number(inside.split(";")[0])

    const to = Number(inside.split(";")[1])

    if (!n || !to) return d.error(`:x: Invalid number in \`$roundTenth[${inside}]\``)

    return {
        code: code.replaceLast(`$roundTenth[${inside}]`, n.toFixed(to))
    }
}