const embed = require("../../handlers/errors")
module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$sendCrosspostingMessage[").length - 1 
    
    const inside = code.split("$sendCrosspostingMessage[")[r].split("]")[0] 
    
    const [msg, ...channelIDs] = inside.split(";") 
    
    const m = await embed(d, msg, true) 
    
    channelIDs.map(id => {
        const channel = d.client.channels.cache.get(id) 
        
        if (channel) channel.send(m.message, m.embed).catch(err => null)
    })
    
    return {
        code: code.replaceLast(`$sendCrosspostingMessage[${inside}]`, "")
    }
}