module.exports = async d => {
    const code = d.command.code

    const r = code.split("$serverBanner").length - 1

    const after = code.split("$serverBanner")[r]

    if (after.startsWith("[")) {
        const inside = after.split("[")[1].split("]")[0]
        
        const options = [
            guildID = d.message.guild.id,
            size = 2048,
            dynamic = "yes"
        ] = inside.split(";")

        const guild = d.client.guilds.cache.get(guildID)

        if (!guild) return d.error(`:x: Invalid guild ID in \`$serverBanner[${inside}]\``)

        return {
            code: code.replaceLast(`$serverBanner[${inside}]`, guild.bannerURL({
                dynamic: dynamic === "yes",
                size: Number(size)
            }))
        }
    } else {
        return {
            code: code.replaceLast(`$serverBanner`, d.message.guild.bannerURL({ dynamic: true }))
        }
    }
}