module.exports = async d => {
    const code = d.command.code
    
    const r = code.split("$serverContentFilter").length - 1

    const after = code.split("$serverContentFilter")[r]
     
    if (after.startsWith("[")) {
        const guildID = after.slice(1).split("]")[0]

        const guild = d.client.guilds.cache.get(guildID)

        if (!guild) return d.error(`:x: Invalid guild ID in \`$serverContentFilter[${guildID}]\``)

        return {
            code: code.replaceLast(`$serverContentFilter[${guildID}]`, guild.explicitContentFilter.split("_").map(word => word.toLowerCase().replace(word.toLowerCase()[0], word[0])).join(" "))
        }
    } else {
        return {
            code: code.replaceLast(`$serverContentFilter`, d.message.guild.explicitContentFilter.split("_").map(word => word.toLowerCase().replace(word.toLowerCase()[0], word[0])).join(" "))
        }
    }
}