const ms = require("ms")
const error = require("../../handlers/errors.js")
const parse = require("parse-ms")
const toParse = require("ms-parser")

module.exports = async d => {
    const code = d.command.code

    const r = code.split("$serverCooldown[").length - 1

    const inside = code.split("$serverCooldown[")[r].split("]")[0]

    const fields = inside.split(";")

    const time = fields.shift()

    const errorMessage = fields.join(";")

    if (!ms(time)) return d.error(`:x: Invalid time '${time}' in \`$serverCooldown[${inside}]\``)

    /*const item = await db.fetch(`${d.command.name}_cooldown_${d.message.guild.id}`) 

    if (item && ms(time) - (Date.now() - item) > 999) {
        return error(d, errorMessage.split("%time%").join(toParse(Object.entries(parse(ms(time) - (Date.now() - item))).map((x, y) => {
            if (x[1] > 0 && y < 4) return `${x[1]}${x[0][0]}`
            else return undefined
        }).filter(e => e).join("")).string))
    } else {
        db.set(`${d.command.name}_cooldown_${d.message.guild.id}`, Date.now())
    }*/
  
  const item = await d.client.db.findOne({
    variable: `${d.command.name}`,
    varID: `cooldowns_${d.message.guild.id}`,
  
})

if (item && ms(time) - (Date.now() - Number(item.value)) > 999) {
    return error(d, errorMessage.split("%time%").join(toParse(Object.entries(parse(ms(time) - (Date.now() - Number(item.value)))).map((x, y) => {
        if (x[1] > 0 && y < 4) return `${x[1]}${x[0][0]}`
        else return undefined
    }).filter(e => e).join("")).string))
} else {
 
    await d.client.db.set({
      value: Date.now(), 
      varID: `cooldowns_${d.message.guild.id}`, 
      variable: d.command.name
    }, {
        variable: d.command.name, 
        varID: `cooldowns_${d.message.guild.id}`
    })
  } 
    //db.set(`${d.command.name}_cooldown_${d.message.guild.id}_${d.message.author.id}`, Date.now())


    return {
        code: code.replaceLast(`$serverCooldown[${inside}]`, "")
    }
}