module.exports = async d => {
    const code = d.command.code

    const r = code.split("$serverIDs").length - 1

    const after = code.split("$serverIDs")[r]

    if (after[0] === "[") {
        const sep = after.slice(1).split("]")[0]

        return {
            code: code.replaceLast(`$serverIDs[${sep}]`, d.client.guilds.cache.map(g => g.id).join(sep))
        }
    } else {
        return {
            code: code.replaceLast(`$serverIDs`, d.client.guilds.cache.map(g => g.id).join(", "))
        }
    }
}