module.exports = async d => {
    const code = d.command.code

    const r = code.split("$serverIcon").length - 1

    const after = code.split("$serverIcon")[r]

    if (after.startsWith("[")) {
        const inside = after.split("[")[1].split("]")[0]

        const options = [
            guildID = d.message.guild.id,
            size = 2048,
            dynamic = "yes"
        ] = inside.split(";")

        const guild = d.client.guilds.cache.get(guildID)

        if (!guild) return d.error(`:x: Invalid guild ID in \`$serverIcon[${inside}]\``)

        return {
            code: code.replaceLast(`$serverIcon[${inside}]`, guild.iconURL({
                dynamic: dynamic === "yes",
                size: Number(size)
            }))
        }
    } else {
        return {
            code: code.replaceLast(`$serverIcon`, d.message.guild.iconURL({ dynamic: true }))
        }
    }
}