
const setChannelVar = async d => {

    const code = d.command.code

    const r = code.split("$setChannelVar").length - 1

    const inside = code.split("$setChannelVar[")[r].split("]")[0]

    const [ variable, value, channelID = d.message.channel.id ] = inside.split(";")

    if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found`)

    
    await d.client.db.set({
      value: value,
      varID:`${channelID}`,
      variable: variable
    }, {
        variable, 
        varID: `${channelID}`
    })
  
    return {
        code: code.replaceLast(`$setChannelVar[${inside}]`, "")
    }
}

module.exports = setChannelVar