
module.exports = async d => {

    const code = d.command.code

    const r = code.split("$setGlobalUserVar").length - 1

    const inside = code.split("$setGlobalUserVar[")[r].split("]")[0]

    const [ variable, value, userID = d.message.author.id ] = inside.split(";")

    if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found`)

    await d.client.db.set({
        variable, 
        varID: `${userID}`, 
        value
    }, {
        varID: `${userID}`, 
        variable 
    })
    return {
        code: code.replaceLast(`$setGlobalUserVar[${inside}]`, "")
    }
}
