
const setMessageVar = async d => {

    const code = d.command.code

    const r = code.split("$setMessageVar").length - 1

    const inside = code.split("$setMessageVar[")[r].split("]")[0]

    const [ variable, value, messageID = d.message.id ] = inside.split(";")

    if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found`)

      
      await d.client.db.set({
      value: value,
      varID:`${messageID}`,
      variable: variable
    }, {
        variable, 
        varID: `${messageID}`
    })
     
   
    

    return {
        code: code.replaceLast(`$setMessageVar[${inside}]`, "")
    }
}

module.exports = setMessageVar