
const setServerVar = async d => {

    const code = d.command.code

    const r = code.split("$setServerVar").length - 1

    const inside = code.split("$setServerVar[")[r].split("]")[0]

    const [ variable, value, guildID = d.message.guild.id ] = inside.split(";")

    if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found`)


    await d.client.db.set({
      value: value,
      varID:`${guildID}`,
      variable: variable
    }, {
        variable, 
        varID: `${guildID}`
    })
   
    

    return {
        code: code.replaceLast(`$setServerVar[${inside}]`, "")
    }
}

module.exports = setServerVar