module.exports = async d => {
    const code = d.command.code

    const r = code.split("$setStatus[").length -1 

    const inside = code.split("$setStatus[")[r].split("]")[0]

    const [
        name,
        type = "PLAYING",
        status = "online"
    ]= inside.split(";")

    const bot = await d.client.user.setPresence({
        activity: {
            name: name,
            type: type.toUpperCase()
        },
        status: status 
    }).catch(err => {})

    if (!bot) return d.error(`:x: Failed to change bot status`)

    return {
        code: code.replaceLast(`$setStatus[${inside}]`, "")
    }
}