
const setUserVar = async d => {

    const code = d.command.code

    const r = code.split("$setUserVar").length - 1

    const inside = code.split("$setUserVar[")[r].split("]")[0]

    const [ variable, value, userID = d.message.author.id ] = inside.split(";")

    if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found`)

    await d.client.db.set({
      value: value,
      varID:`${d.message.guild.id}_${userID}`,
      variable: variable
    }, {
      varID: `${d.message.guild.id}_${userID}`, 
      variable 
    })

    return {
        code: code.replaceLast(`$setUserVar[${inside}]`, "")
    }
}

module.exports = setUserVar