
const setVar = async d => {

    const code = d.command.code

    const r = code.split("$setVar").length - 1

    const inside = code.split("$setVar[")[r].split("]")[0]

    const [ variable, value ] = inside.split(";")

    if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found`)

        const item = await d.client.db.findOne({
        varID:`0`,
        variable:variable
      })

       
    await d.client.db.set({
      value: value,
      varID:`0`,
      variable: variable
    }, {
      varID:`0`,
      variable:variable
    })
    
    
    return {
        code: code.replaceLast(`$setVar[${inside}]`, "")
    }
}

module.exports = setVar