module.exports = d => {
 
  const code = d.command.code
  
  const r = code.split("$splitText").length - 1
  
  const inside = code.split("$splitText[")[r].split("]")[0]
  
  const n =Number(inside)
  
  if (isNaN(n) || n < 1) return d.error(`âŒ Invalid number in \`$splitText[${inside}]\``)
  
  return {
    code: code.replaceLast(`$splitText[${inside}]`, d.array[n - 1] ? d.array[n - 1].removeBrackets() : "") 
  } 
} 