const sub = async d => {

    const code = d.command.code

    const r = code.split("$sub").length - 1

    const inside = code.split("$sub[")[r].split("]")[0]

    const fields = inside.split(";")

    if (fields.some(n => isNaN(Number(n)))) return d.error(`:x: Invalid number in \`$sub[${inside}]\``)

    const n = fields.reduce((x, y) => Number(x) - Number(y))

    return {
        code: code.replaceLast(`$sub[${inside}]`, n)
    }
}

module.exports = sub