module.exports = async d => {
    const code = d.command.code

    const r = code.split("$systemChannelID").length - 1

    const after = code.split("$systemChannelID")[r]

    if (after[0] === "[") {
        const guildID = after.slice(1).split("]")[0]

        const guild = d.client.guilds.cache.get(guildID)

        if (!guild) return d.error(`:x: Invalid guild ID in \`$systemChannelID[${guildID}]\``)

        return {
            code: code.replaceLast(`$systemChannelID[${guildID}]`, guild.systemChannelID || "")
        }
    } else {
        return {
            code: code.replaceLast(`$systemChannelID`, d.message.guild.systemChannelID || "")
        }
    }
}