module.exports = d => {
  
  const code = d.command.code
  
  const r = code.split("$textSplit[").length - 1
  
  const inside = code.split("$textSplit[")[r].split("]")[0]
  
  const [ text, separator = " " ] = inside.split(";")
  
  return {
    code: code.replaceLast(`$textSplit[${inside}]`, ""),
    array: text.deleteBrackets().split(separator) 
  } 
}
