module.exports = async d => {
    const code = d.command.code

    const r = code.split("$toLocaleUppercase[").length - 1

    const inside = code.split("$toLocaleUppercase[")[r].split("]")[0]

    return {
        code: code.replaceLast(`$toLocaleUppercase[${inside}]`, inside.split(" ").map(word => word.toLowerCase().replace(word.toLowerCase()[0], word[0].toUpperCase())).join(" "))
    }
}