module.exports = async d => {
    const code = d.command.code

    const r =code.split("$truncate[").length - 1

    const inside = code.split("$truncate[")[r].split("]")[0]

    const n = Number(inside)

    if (!n) return d.error(`:x: Invalid number in \`$truncate[${inside}]\``)

    return {
        code: code.replaceLast(`$truncate[${inside}]`, Math.trunc(n))
    }
}