const ms = require("parse-ms")

const uptime = (d) => {
  
       let code = d.command.code   

      /* let days = Math.floor(d.client.uptime / 86400000);
      let hours = Math.floor(d.client.uptime / 3600000) % 24;
      let minutes = Math.floor(d.client.uptime / 60000) % 60;
      let seconds = Math.floor(d.client.uptime / 1000) % 60;
      */

      //this is a pain L

    return {
        code: code.replaceLast(`$uptime`, Object.entries(ms(d.client.uptime)).map((x, y) => {
            if (x[1] && y < 4) return `${x[1]}${x[0][0]}`
            else return undefined
        }).filter(e => e).join(" "))
    }
}

module.exports = uptime