module.exports = async d => {
  
  const code = d.command.code
  
  const r = code.split("$userExists").length - 1
  
  const inside = code.split("$userExists[")[r].split("]")[0]
  
  const user = await d.client.users.fetch(inside).catch(err => {})
  
  return {
    code: code.replaceLast(`$userExists[${inside}]`, user ? true : false) 
  } 
}

