module.exports = async d => {

    const code = d.command.code

    const r = code.split("$userID[").length - 1
    
    const username = code.split("$userID[")[r].split("]")[0]

    let user = d.client.users.cache.find(u => u.username.toLowerCase() === username.toLowerCase())

    return {
        code: code.replaceLast(`$userID[${username}]`, user ? user.id : "")
    }
}