module.exports = async d => {
    const code = d.command.code

    const r = code.split("$userTag").length - 1

    const after = code.split("$userTag")[r].split("]")[0]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]

        const user = await d.client.users.fetch(inside).catch(err => {})

        if (!user) return d.error(`:x: Invalid user ID in \`$userTag[${inside}]\``)

        return {
            code: code.replaceLast(`$userTag[${inside}]`, user.tag.removeBrackets())
        }
    } else {
        return {
            code: code.replaceLast(`$userTag`, d.message.author.tag.removeBrackets())
        }
    }
}