module.exports = async d => {
    const code = d.command.code

    const r = code.split("$voiceID").length - 1

    const after = code.split("$voiceID")[r]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]

        const member = await d.message.guild.members.fetch(inside).catch(err => {})

        if (!member) return d.error(`:x: Invalid user ID in \`$voiceID[${inside}]\``)

        return {
            code: code.replaceLast(`$voiceID[${inside}]`, member.voice.channel ? member.voice.channel.id : "")
        }
    } else {
        return {
            code: d.command.code.replaceLast(`$voiceID`, d.message.member.voice.channel ? d.message.member.voice.channel.id : "")
        }
    }
}