const ms = require("ms")

module.exports = async d=>{
 
  const code = d.command.code
  
  const r = code.split("$wait").length - 1
  
  const time = code.split("$wait[")[r].split("]")[0]
  
  await new Promise(e => setTimeout(e, ms(time)))
  
  return {
    code: code.replaceLast(`$wait[${time}]`, "") 
  } 
} 