const fetch = require("node-fetch")
const json = require("../../package.json")
module.exports = async() => {
const get = await fetch("https://api.dbdjs.repl.co/package/version", {method:"GET"})
const response = await get.json()
if(json.version !== response.version) {
    console.warn("\x1b[33mWARNING: \x1b[0mThe current DBD.JS Version is not the latest or the right version, update it now with \"npm install dbd.js@latest\"")
}
}